function loadTweets(){$.getJSON("api/twitter/recent",function(i){var t=0;for(var t in i.data){var e=i.data[t];e.created_at=Date.parse(e.created_at),tweets.push(e),addStreamItemToHtml(e,t)}})}function addStreamItemToHtml(i,t){console.log(i);var e="",a=Math.round((now-i.created_at)/1e3),n=i.user.profile_image_url;e+='<div class="oneStream">',e+='<span class="time" id="time'+t+'">'+hMSString(a,!1)+"</span>",e+='<img class="twitterProfileImage" src="'+n+'" />',e+="</div>"}function hMSString(i,t){var e=parseInt(i/3600)%24,a=parseInt(i/60)%60,n=i%60,s=10>n?"0":"",r=10>a?"0":"",c=10>e?"0":"";return t?c+e+":"+r+a+":"+s+n:r+a+":"+s+n}var tweets=[],now=Date.now();$(function(){$("#liveButton").bind("click",function(){$("div.live").css("visibility","visible"),$("div.schedule").css("visibility","hidden")}),$("#comingButton").bind("click",function(){$("div.live").css("visibility","hidden"),$("div.schedule").css("visibility","visible")}),loadTweets()});