function loadTweets(){$.ajax({url:"http://whatsonmeerkat.com/api/twitter/recent",jsonp:"callback",dataType:"jsonp",success:function(e){for(var t in e.data){var a=e.data[t];a.created_at=Date.parse(a.created_at),tweets.push(a),addStreamItemToHtml(a,t)}timer=setInterval(refreshAllTimes,1e3)}}),console.log("22222")}function addStreamItemToHtml(e,t){var a="",n=Math.round((now-e.created_at)/1e3),s=e.user.profile_image_url;a+='<a href="'+e.entities.urls[0].expanded_url+'">',a+='<div class="oneStream">',a+='<span class="time" id="time'+t+'">'+hMSString(n,!1)+"</span>",a+='<img class="twitterProfileImage" src="'+s+'" />',a+='<span class="text"><b>'+e.user.name+"</b> "+e.text+"</span>",a+="</div>",a+="</a>",$(a).appendTo("div.live")}function hMSString(e,t){var a=parseInt(e/3600)%24,n=parseInt(e/60)%60,s=e%60,i=10>s?"0":"",r=10>n?"0":"",o=10>a?"0":"";return t?o+a+":"+r+n+":"+i+s:r+n+":"+i+s}function refreshAllTimes(){now=Date.now(),tweets.forEach(function(e,t){var a=Math.round((now-e.created_at)/1e3);$("#time"+t).text(hMSString(a,!1))})}var tweets=[],now=Date.now(),timer;$(function(){$("#liveButton").bind("click",function(){$("div.live").css("display","block"),$("div.schedule").css("display","none")}),$("#comingButton").bind("click",function(){$("div.live").css("display","none"),$("div.schedule").css("display","block")}),loadTweets()});